import {React, Fragment} from 'react'
import {InfoConsumer} from '../Context';
import styled from 'styled-components';

function Details() {
    return(
        <InfoConsumer>
            {/*{data => {
              const {
                id,
                headerDetailTitle,
                headerDetailSubTitle,
                img,
                description
            } = data.infoDetails;*/}
            return (
                <Fragment>
                    <HeaderDetails className="conteiner-fluid align-items-center">
                        <h1 className="display-1 font-weight-bold">header title</h1>
                        <h4 className="display-5">header subtitle</h4>
                        <p>header text</p>
                        {/* Social Icons */}
                        <div className="container mt-5">
                            <div className="row justify-content-center">
                                <div className="col-2">
                                    <i className="fab fa-facebook-f" />
                                </div>
                                <div className="col-2">
                                    <i className="fab fa-twitter" />
                                </div>
                                <div className="col-2">
                                    <i className="fab fa-google-plus-g" />
                                </div>
                                <div className="col-2">
                                    <i className="fab fa-reddit" />
                                </div>
                                <div className="col-2">
                                    <i className="fab fa-whatsapp" />
                                </div>
                                <div className="col-2">
                                    <i className="fab fa-facebook-messenger" />
                                </div>
                            </div>
                        </div>
                    </HeaderDetails>
                </Fragment>
            );
            }}
        </InfoConsumer>
    )
}

const HeaderDetails = styled.header`
  background: linear-gradient(rgba(109, 109, 109), rgba(255, 255, 255));
  height: 100vh;
  text-transform: uppercase;
  color: var(--mainWhite);
  text-align: center;
  h1 {
    padding-top: 10%;
    color: var(--mainDark);
  }
  h4 {
    color: var(--mainDark);
  }
  p {
    padding-left: 10%;
    padding-right: 10%;
    margin-bottom: 10%;
    color: var(--mainDark);
  }
  i {
    font-size: 1.875rem;
    color: var(--mainDark);
  }
  i:hover {
    color: var(--mainBlue);
    cursor: pointer;
  }
  .nav-item {
    height: 18.75rem;
  }
  @media (max-width: 760px) {
    h1,
    h4 {
      color: var(--mainWhite);
    }
  }
`;
export default Details;
