import React from 'react'
import Plot from 'react-plotly.js';
import styled from 'styled-components';
import { InfoConsumer } from '../Context';

function Skills() {

    return (
        <InfoConsumer>
        {value => {
          const {
            headerDetailTitle,
            headerDetailSubTitle,
            description,
          } = value.infoDetails;

          return (
            <React.Fragment>
              <SkillsDetails className="conteiner-fluid align-items-center">
              <Plot
        data={[
          {
            x: [1, 2, 3],
            y: [2, 6, 3],
            type: 'scatter',
            mode: 'lines+markers',
            marker: {color: 'red'},
          },
          {type: 'bar', x: [1, 2, 3], y: [2, 5, 3]},
        ]}
        layout={{
            title: 'A Fancy Plot',
            autosize: true
        } }
      />
              </SkillsDetails>
            </React.Fragment>
          );
        }}
      </InfoConsumer>
    )
}
export default Skills;

const SkillsDetails = styled.body`
  background: linear-gradient(rgba(109, 109, 109), rgba(255, 255, 255));
  height: 100vh;
  text-transform: uppercase;
  color: var(--mainWhite);
  text-align: center;
  h1 {
    padding-top: 10%;
    color: var(--mainDark);
  }
  h4 {
    color: var(--mainDark);
  }
  p {
    padding-left: 10%;
    padding-right: 10%;
    margin-bottom: 2%;
    color: var(--mainDark);
  }
  button {
    color: var(--mainWhite);
    background: var(--mainDark);
    margin-bottom: 8%;
  }
  button:hover {
    background: var(--mainBlue)
  }
  i {
    font-size: 1.875rem;
    color: var(--mainDark);
  }
  i:hover {
    color: var(--mainBlue);
    cursor: pointer;
  }
  .nav-item {
    height: 18.75rem;
  }
  @media (max-width: 760px) {
    h1,
    h4 {
      color: var(--mainWhite);
    }
  }
`;
