import React from 'react'
import Plot from 'react-plotly.js';
import styled from 'styled-components';
import { InfoConsumer } from '../Context';


var data = [{
    type: "sunburst",
    labels: ["",
             "Spring",
             "Core",
             "JEE",
             "JPI",
             "EJB",
             "Spring-Boot","Spring-Web"],
    parents: [
      "Skills", 
      "Java",
      "Java",
      "Java",
      "JEE",
      "JEE",
      "Spring",
      "Spring"],
    values:  [12, 10, 20, 6, 6, 4, 4],
    outsidetextfont: {size: 20, color: "#377eb8"},
    leaf: {opacity: 0.4},
    marker: {line: {width: 2}},
  }];

  var layout = {
    margin: {l: 0, r: 0, b: 0, t: 0},
    width: 500,
    height: 500
  };

function Skills() {

    return (
        <InfoConsumer>
        {value => {
          const {
            headerDetailTitle,
            headerDetailSubTitle,
            description,
          } = value.infoDetails;

          return (
            <React.Fragment>
              <SkillsDetails className="conteiner-fluid align-items-center">
              <Plot data={data} layout={layout}/>
              </SkillsDetails>
            </React.Fragment>
          );
        }}
      </InfoConsumer>
    )
}
export default Skills;

const SkillsDetails = styled.body`
  background: linear-gradient(rgba(109, 109, 109), rgba(255, 255, 255));
  height: 100vh;
  text-transform: uppercase;
  color: var(--mainWhite);
  text-align: center;
  h1 {
    padding-top: 10%;
    color: var(--mainDark);
  }
  h4 {
    color: var(--mainDark);
  }
  p {
    padding-left: 10%;
    padding-right: 10%;
    margin-bottom: 2%;
    color: var(--mainDark);
  }
  button {
    color: var(--mainWhite);
    background: var(--mainDark);
    margin-bottom: 8%;
  }
  button:hover {
    background: var(--mainBlue)
  }
  i {
    font-size: 1.875rem;
    color: var(--mainDark);
  }
  i:hover {
    color: var(--mainBlue);
    cursor: pointer;
  }
  .nav-item {
    height: 18.75rem;
  }
  @media (max-width: 760px) {
    h1,
    h4 {
      color: var(--mainWhite);
    }
  }
`;
